import React from 'react';
import api from '../services/api';
import { Link } from 'react-router-dom';

class LibroList extends React.Component {
  state = { libros: [] };

  componentDidMount() {
    this.fetch();
  }

  fetch = () => {
    api.get('/libros').then(res => this.setState({ libros: res.data })).catch(() => {});
  }

  remove = (id) => {
    if (!window.confirm('Eliminar registro?')) return;
    api.delete(`/libros/${id}`).then(() => this.fetch()).catch(() => {});
  }

  render() {
    return (
      <div>
        <h2>Listado de libros</h2>
        <ul>
          {this.state.libros.map(l => (
            <li key={l._id} style={{ marginBottom: 10 }}>
              {l.portada && <img src={`http://localhost:5000${l.portada}`} alt="" width="60" style={{marginRight:10}} />}
              <strong>{l.nombreLibro}</strong> - {l.autor} ({l.editorial})
              <div>
                <Link to={`/libro/${l._id}`} style={{ marginRight: 10 }}>Detalle</Link>
                <Link to={`/editar/${l._id}`} style={{ marginRight: 10 }}>Editar</Link>
                <button onClick={() => this.remove(l._id)}>Eliminar</button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}

export default LibroList;
