import React from 'react';
import api from '../services/api';

class Home extends React.Component {
  state = { last: [] };

  componentDidMount() {
    api.get('/libros/last3').then(res => this.setState({ last: res.data })).catch(() => {});
  }

  render() {
    return (
      <div>
        <h2>Bienvenido a Apilados - Admin Libros</h2>
        <h3>Ãšltimos 3 libros</h3>
        <ul>
          {this.state.last.map(l => (
            <li key={l._id}>
              <img src={`http://localhost:5000${l.portada}`} alt="" width="50" style={{marginRight:10}} />
              {l.nombreLibro} - {l.autor} ({l.editorial})
            </li>
          ))}
        </ul>
      </div>
    );
  }
}

export default Home;
