import React from 'react';
import api from '../services/api';
import { Link } from 'react-router-dom';

class Search extends React.Component {
  state = { q: '', results: [] };

  handle = (e) => this.setState({ q: e.target.value });

  submit = (e) => {
    e.preventDefault();
    api.get(`/libros/search?q=${encodeURIComponent(this.state.q)}`).then(res => this.setState({ results: res.data })).catch(() => {});
  }

  render() {
    return (
      <div>
        <h2>Buscar libros</h2>
        <form onSubmit={this.submit}>
          <input value={this.state.q} onChange={this.handle} placeholder="Nombre, autor o editorial" />
          <button type="submit">Buscar</button>
        </form>
        <ul>
          {this.state.results.map(r => (
            <li key={r._id}>
              {r.portada && <img src={`http://localhost:5000${r.portada}`} alt="" width="50" style={{marginRight:10}} />}
              <Link to={`/libro/${r._id}`}>{r.nombreLibro}</Link> - {r.autor} ({r.editorial})
            </li>
          ))}
        </ul>
      </div>
    );
  }
}

export default Search;
